mapper.select:
SELECT name
FROM user
WHERE user_no = #{userNo}


SELECT *
FROM user u JOIN approval a
WHERE a.approval_no = #{approvalNo} AND u.user_no != a.checker1;

mapper.update:
UPDATE approval
SET title = #{title}, content = #{content}, checker1 = (SELECT user_no FROM user WHERE name LIKE #{checker1}) 
WHERE approval_no = #{approvalNo};

UPDATE approval
SET check_date = curdate(), status = #{status}
WHERE approval_no = #{approvalNo}
