mapper.select:
select notice_no, date, content, title
from notice
order by date desc;

SELECT notice_no, title, date, content
FROM notice
WHERE title LIKE concat('%', #{title}, '%');

SELECT notice_no, title, date, content
FROM notice
WHERE content LIKE concat('%', #{content}, '%');

SELECT notice_no, title, date, content
FROM notice
WHERE notice_no LIKE concat('%', #{noticeNo}, '%');
